<script>
import InventoryItem from "./InventoryItem.svelte";
import InventoryInfo from "./InventoryInfo.svelte";
export let InventoryItems;

let displayList = InventoryItems;
let addItemForm = false;
let searchterm;
let additemprodnum;
let additemproddesc;
let customid = 10000;

// Helper functions
const updateList = (obj) => {
    displayList = obj;
}
const resetSearch = () => {
    searchterm = "";
    updateList(InventoryItems);
}
const toggleAddItemForm = () => {
    addItemForm = !addItemForm;
}


const itemFind = (arr, search) => {
    let regex = new RegExp(search, "i");
    let results = [];
    for(var i = 0; i < arr.length; i++){
        let keymatch = false;
        for (let key in arr[i]){
            if (typeof arr[i][key] == "string"){
                if(arr[i][key].match(regex)){
                    keymatch = true;
                }
            }
        }
        keymatch ? results.push(arr[i]) : false;
    }
    return results;
}
const handleSearch = () => {
    let found = itemFind(InventoryItems, searchterm);
    found ? updateList(found) : updateList(InventoryItems);
}
const handleAddItem = () => {
    let pn;
    let pd;
    if(additemprodnum || additemproddesc){
        pn = additemprodnum ? additemprodnum : "";
        pd = additemproddesc ? additemproddesc : "";
        InventoryItems.push({
            productName: pd,
            productNumber: pn,
            custom: true,
            itemid: customid
        })
        customid++;
        additemprodnum = "";
        additemproddesc = "";
        resetSearch();
        toggleAddItemForm();
    }
}
const handleDeleteItem = (itemId) => {
    let resultArray = InventoryItems.filter((item) => {
        return item.itemid != itemId;
    })
    InventoryItems = resultArray;
    updateList(InventoryItems);
}
</script>
<main>
    <div>
        <InventoryInfo />
        <input type="text" placeholder="Search for product..." on:change={handleSearch} bind:value={searchterm}/>
        {#each displayList as Item}
            <InventoryItem
                productName={Item.productName}
                productNumber={Item.productNumber}
                custom={Item.custom}
                itemid={Item.itemid}
                expectedQty={Item.expectedQty}

                handleDeleteItem={handleDeleteItem}
            />
        {/each}
        {#if displayList.length < 1 && !addItemForm}
            <div>
                <h4>NOT FOUND</h4>
                <p>There was no product matching that search.<br />If you're sure it should be here, click below to add it.</p>
                <button on:click={toggleAddItemForm}>Add Item to Audit</button>
                <button on:click={resetSearch}>Clear Search</button>
            </div>
        {/if}
        {#if displayList.length < 1 && addItemForm}
            <div class="add-item-form">
                <h3>Add an Item</h3>
                <input type="text" placeholder="Product Number" bind:value={additemprodnum} />
                <input type="text" placeholder="Product Description" bind:value={additemproddesc} />
                <button on:click={toggleAddItemForm}>Cancel</button>
                <button on:click={handleAddItem}>Add</button>
            </div>
        {/if}
    </div>
</main>
